        -- CLIENTE DOMICILIO
        LPAD(UPPER(' '), 5, ' ') ||  -- Dirección domicilio: Tipo de Via
        LPAD(UPPER(NVL(cli.B06_DIRECCION,' ')), 30, ' ') ||  -- Dirección domicilio: Nombre de Via
