--------------------------------------------------------
--  DDL for Procedure BNPD_13_ASIGNACION
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "BN_SATE"."BNPD_13_ASIGNACION" 
IS 
    -- Definición del tipo de cursor de referencia
    TYPE t_cursor IS REF CURSOR;
    c_lineas t_cursor;  -- Declaración del cursor
    
    -- Variables para almacenar las líneas obtenidas del cursor
    v_linea1 VARCHAR2(1021);
    v_linea2 VARCHAR2(1021);
    
    -- Variable para contar el número de registros
    v_numero_registros NUMBER := 0;
BEGIN
    -- Abrir el cursor y generar las líneas
    OPEN c_lineas FOR
        SELECT 
            -- Generación de la primera parte del registro con LPAD
            LPAD(
                LPAD(UPPER('B'), 1, '0') ||
                LPAD(UPPER('0302'), 4, '0') ||
                LPAD(UPPER('4220000080010000'), 16, '0') ||
                LPAD(UPPER('19'), 2, '0') ||
                LPAD(NVL(tar.b05_num_cuenta, '9'), 19, '0') ||
                TO_CHAR(TO_DATE('19/02/2024', 'DD/MM/YYYY'), 'MMDDHHMISS') ||
                LPAD(
                (SELECT b18_trace_code FROM bnsate18_trace_trama WHERE b05_num_tarjeta = tar.b05_num_tarjeta) , 6, '0') ||
                LPAD(UPPER('11'), 2, '0') ||
                LPAD(UPPER('00000000000'), 11, '0') ||
                LPAD(UPPER('081'), 3, '0') 
             , 74, '0') AS PART1,
            -- Generación de la segunda parte del registro basada en el valor de l.i
         
               RPAD(LPAD('0117', 4, '0') || '0' || '0000' || 'N' || asi.b04_linea ||  TO_CHAR(asi.b04_fecha_inicio_linea, 'DDMMYYYY') || TO_CHAR(asi.b04_fecha_fin_linea, 'DDMMYYYY')|| ' ' || ' ' || '0050000' , 926, ' ')
               AS linea
        FROM bnsate05_tarjeta tar
        JOIN BNSATE04_ASIGNACION asi
        ON tar.b05_id_tar = asi.b05_id_tar
   
        where tar.B05_NUM_TARJETA is not null and 
asi.B04_FECHA_REGISTRO = TO_DATE('27/08/2024', 'DD/MM/YYYY')
        ORDER BY tar.b05_num_cuenta;

    -- Primera línea de encabezado (header) con formato específico
    SELECT COUNT(*) * 1 INTO v_numero_registros FROM bnsate05_tarjeta where B05_NUM_TARJETA is not null;
    DBMS_OUTPUT.PUT_LINE('A' || '019' ||TO_CHAR(TO_DATE('19/02/2024', 'DD/MM/YYYY'), 'DDMMYYY') || LPAD(v_numero_registros, 6, '0') || LPAD(' ', 982, ' '));

    -- Recorrer el cursor e imprimir las líneas generadas
    LOOP
        FETCH c_lineas INTO v_linea1, v_linea2;
        EXIT WHEN c_lineas%NOTFOUND;  -- Salir del bucle cuando no haya más registros
        DBMS_OUTPUT.PUT_LINE(v_linea1 || v_linea2);  -- Imprimir la línea generada
    END LOOP;

    -- Cerrar el cursor después de usarlo
    CLOSE c_lineas;

EXCEPTION
    -- Manejo de excepciones
    WHEN OTHERS THEN
        IF c_lineas%ISOPEN THEN
            CLOSE c_lineas;  -- Cerrar el cursor en caso de error
        END IF;
        RAISE;  -- Propagar la excepción
END BNPD_13_ASIGNACION;

